// src/components/Dashboard.js - Versão Completa Final
import React, { useState, useEffect } from 'react';
import * as XLSX from 'xlsx';
import './Dashboard.css';

const Dashboard = ({ user, onLogout }) => {
  // Estados principais
  const [qrCode, setQrCode] = useState(null);
  const [isConnecting, setIsConnecting] = useState(false);
  const [connectionStatus, setConnectionStatus] = useState(user.whatsappStatus || 'Desconectado');
  const [instanceCreated, setInstanceCreated] = useState(false);
  const [statusCheckInterval, setStatusCheckInterval] = useState(null);
  const [phoneNumber, setPhoneNumber] = useState(user.phoneNumber || '');
  const [instanceExists, setInstanceExists] = useState(false);
  const [isDeleting, setIsDeleting] = useState(false);
  const [activeTab, setActiveTab] = useState('dashboard');
  const [instanceInfo, setInstanceInfo] = useState(null);

  // Estados para grupos
  const [groups, setGroups] = useState([]);
  const [selectedGroup, setSelectedGroup] = useState('');
  const [newGroupName, setNewGroupName] = useState('');
  const [newGroupParticipants, setNewGroupParticipants] = useState([]);
  const [isCreatingGroup, setIsCreatingGroup] = useState(false);
  const [selectedGroupForDetails, setSelectedGroupForDetails] = useState(null);
  const [groupDetails, setGroupDetails] = useState(null);
  const [isLoadingDetails, setIsLoadingDetails] = useState(false);
  const [isExporting, setIsExporting] = useState(false);
  const [isLeavingGroup, setIsLeavingGroup] = useState(false);

  // Estados para mensagens
  const [contacts, setContacts] = useState([]);
  const [selectedContacts, setSelectedContacts] = useState([]);
  const [message, setMessage] = useState('');
  const [isSending, setIsSending] = useState(false);
  const [sendingStatus, setSendingStatus] = useState('');
  const [selectedGroupForBulk, setSelectedGroupForBulk] = useState('');
  const [isSendingToParticipants, setIsSendingToParticipants] = useState(false);

  // Estados para UI mobile
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');

  // Estados para configurações anti-bloqueio
  const [antiBanSettings, setAntiBanSettings] = useState({
    enableSmartDelays: true,
    enableUserAgentRotation: true,
    enableBatchProcessing: true,
    enableProgressiveDelay: true,
    enableQualityMonitoring: true,
    excludeOwnNumber: true,
    showOnlyOwnedGroups: true, // Nova configuração para mostrar apenas grupos próprios
    minDelay: 3000,
    maxDelay: 8000,
    batchSize: 3,
    batchDelay: 15000,
    maxDailyMessages: 500,
    maxHourlyMessages: 50
  });

  // Estados para monitoramento
  const [messageStats, setMessageStats] = useState({
    sentToday: 0,
    sentThisHour: 0,
    successRate: 100,
    excludedOwnNumber: 0,
    totalDispatches: 0, // Novo contador para disparos totais
    lastResetDate: new Date().toDateString(),
    lastResetHour: new Date().getHours()
  });

  // Estado para armazenar o número logado
  const [loggedNumber, setLoggedNumber] = useState(null);

  // Estados para API de disparos
  const [isUpdatingDispatch, setIsUpdatingDispatch] = useState(false);

  // Constantes para controle de delay inteligente melhorado
  const MESSAGE_DELAY = {
    MIN: antiBanSettings.minDelay,
    MAX: antiBanSettings.maxDelay,
    BATCH_SIZE: antiBanSettings.batchSize,
    BATCH_DELAY: antiBanSettings.batchDelay,
    PROGRESSIVE_MULTIPLIER: 1.2,
    ERROR_PENALTY_MULTIPLIER: 2.0,
    SUCCESS_BONUS_MULTIPLIER: 0.8
  };

  const API_BASE_URL = 'https://evo.fluxodigitaltech.com.br';
  const API_KEY = user.apiKey || 'lcV71zmct4xwueG7nd7y0uw9mcAOYBTKN3EbAu/1OIXU2QKYvmILUAFlhF2WoclioIvoL1fORjBaFrX0TWqY3g==';

  // API de contagem de disparos
  const DISPATCH_API_URL = 'https://api.steinhq.com/v1/storages/68ad1d03affba40a62f25e10/disparo';

  // User Agents para rotação
  const USER_AGENTS = [
    'WhatsApp/2.23.24.76 A',
    'WhatsApp/2.23.20.0 A',
    'WhatsApp/2.23.19.75 A',
    'WhatsApp/2.23.18.76 A',
    'WhatsApp/2.23.17.73 A'
  ];

  // Efeitos
  useEffect(() => {
    checkInstanceStatus();
    initializeMessageStats();
    loadDispatchCount(); // Carregar contagem de disparos

    return () => {
      if (statusCheckInterval) {
        clearInterval(statusCheckInterval);
      }
    };
  }, []);

  useEffect(() => {
    if (user.firstLogin && user.whatsappStatus !== 'Conectado' && !instanceCreated) {
      createInstanceAndGenerateQR();
    }
  }, [user, instanceCreated]);

  useEffect(() => {
    if (activeTab === 'groups' && connectionStatus === 'Conectado') {
      loadGroups();
    }
  }, [activeTab, connectionStatus]);

  useEffect(() => {
    if (activeTab === 'messages' && connectionStatus === 'Conectado') {
      loadContacts();
    }
  }, [activeTab, connectionStatus]);

  // Função para extrair número do ID do WhatsApp
  const extractPhoneNumber = (whatsappId) => {
    if (!whatsappId) return null;
    return whatsappId.split('@')[0];
  };

  // Função para normalizar números para comparação
  const normalizePhoneNumber = (number) => {
    if (!number) return '';
    const cleaned = number.toString().replace(/\D/g, '');
    if (cleaned.startsWith('55') && cleaned.length > 11) {
      return cleaned.substring(2);
    }
    return cleaned;
  };

  // Função para verificar se um número é o próprio número logado
  const isOwnNumber = (targetNumber) => {
    if (!antiBanSettings.excludeOwnNumber || !loggedNumber) return false;

    const normalizedTarget = normalizePhoneNumber(extractPhoneNumber(targetNumber));
    const normalizedLogged = normalizePhoneNumber(extractPhoneNumber(loggedNumber));

    const matches = [
      normalizedTarget === normalizedLogged,
      normalizedTarget === normalizePhoneNumber(phoneNumber),
      extractPhoneNumber(targetNumber) === extractPhoneNumber(loggedNumber),
      targetNumber === loggedNumber,
      targetNumber.includes(normalizedLogged),
      normalizedLogged.includes(normalizedTarget)
    ];

    return matches.some(match => match);
  };

  // Função para verificar se o usuário é dono do grupo
  const isGroupOwner = (group) => {
    if (!loggedNumber || !group.owner) return false;

    const normalizedOwner = normalizePhoneNumber(extractPhoneNumber(group.owner));
    const normalizedLogged = normalizePhoneNumber(extractPhoneNumber(loggedNumber));

    return normalizedOwner === normalizedLogged;
  };

  // Função para filtrar próprio número de uma lista
  const filterOwnNumber = (numbersList) => {
    if (!antiBanSettings.excludeOwnNumber) return numbersList;

    const filtered = numbersList.filter(number => !isOwnNumber(number));
    const excludedCount = numbersList.length - filtered.length;

    if (excludedCount > 0) {
      setMessageStats(prev => ({
        ...prev,
        excludedOwnNumber: prev.excludedOwnNumber + excludedCount
      }));
      console.log(`🚫 Excluído próprio número ${excludedCount} vez(es) da lista de envio`);
    }

    return filtered;
  };

  // Função para carregar contagem de disparos da API
  const loadDispatchCount = async () => {
    try {
      const response = await fetch(DISPATCH_API_URL);
      if (response.ok) {
        const data = await response.json();
        const userRecord = data.find(record => record.id === user.instancia);

        if (userRecord && userRecord.disparo) {
          setMessageStats(prev => ({
            ...prev,
            totalDispatches: parseInt(userRecord.disparo) || 0
          }));
        }
      }
    } catch (error) {
      console.error('Erro ao carregar contagem de disparos:', error);
    }
  };

  // Função para atualizar contagem de disparos na API
  const updateDispatchCount = async (incrementBy = 1) => {
    if (isUpdatingDispatch) return;

    setIsUpdatingDispatch(true);
    try {
      // Primeiro, verificar se o ID já existe
      const getResponse = await fetch(DISPATCH_API_URL);
      if (!getResponse.ok) throw new Error('Erro ao buscar dados de disparo');

      const data = await getResponse.json();
      const userRecord = data.find(record => record.id === user.instancia);

      if (userRecord) {
        // Atualizar registro existente (PUT)
        const currentCount = parseInt(userRecord.disparo) || 0;
        const newCount = currentCount + incrementBy;

        const putResponse = await fetch(`${DISPATCH_API_URL}/${user.instancia}`, {
          method: 'PUT',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            disparo: newCount.toString()
          })
        });

        if (putResponse.ok) {
          setMessageStats(prev => ({
            ...prev,
            totalDispatches: newCount
          }));
          console.log(`📊 Contagem de disparos atualizada: ${newCount}`);
        }
      } else {
        // Criar novo registro (POST)
        const postResponse = await fetch(DISPATCH_API_URL, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            id: user.instancia,
            disparo: incrementBy.toString()
          })
        });

        if (postResponse.ok) {
          setMessageStats(prev => ({
            ...prev,
            totalDispatches: incrementBy
          }));
          console.log(`📊 Novo registro de disparos criado: ${incrementBy}`);
        }
      }
    } catch (error) {
      console.error('Erro ao atualizar contagem de disparos:', error);
    } finally {
      setIsUpdatingDispatch(false);
    }
  };

  // Inicializar estatísticas de mensagens
  const initializeMessageStats = () => {
    const today = new Date().toDateString();
    const currentHour = new Date().getHours();

    if (messageStats.lastResetDate !== today) {
      setMessageStats(prev => ({
        ...prev,
        sentToday: 0,
        excludedOwnNumber: 0,
        lastResetDate: today
      }));
    }

    if (messageStats.lastResetHour !== currentHour) {
      setMessageStats(prev => ({
        ...prev,
        sentThisHour: 0,
        lastResetHour: currentHour
      }));
    }
  };

  // Funções utilitárias melhoradas
  const getRandomUserAgent = () => {
    return USER_AGENTS[Math.floor(Math.random() * USER_AGENTS.length)];
  };

  const getSmartDelay = (messageIndex, errorCount = 0, successCount = 0) => {
    let baseDelay = Math.floor(Math.random() * (MESSAGE_DELAY.MAX - MESSAGE_DELAY.MIN + 1)) + MESSAGE_DELAY.MIN;

    if (antiBanSettings.enableProgressiveDelay) {
      const progressiveMultiplier = Math.pow(MESSAGE_DELAY.PROGRESSIVE_MULTIPLIER, Math.floor(messageIndex / 10));
      baseDelay *= progressiveMultiplier;
    }

    if (errorCount > 0) {
      baseDelay *= Math.pow(MESSAGE_DELAY.ERROR_PENALTY_MULTIPLIER, errorCount);
    }

    if (successCount > 5) {
      baseDelay *= MESSAGE_DELAY.SUCCESS_BONUS_MULTIPLIER;
    }

    const variation = (Math.random() - 0.5) * 0.3;
    baseDelay *= (1 + variation);

    return Math.min(baseDelay, 30000);
  };

  const validateAndFormatPhoneNumber = (number) => {
    if (!number) return null;
    let formattedNumber = number.replace(/[^\d@\.\-]/g, '');
    if (!/^\d+/.test(formattedNumber)) return null;
    return formattedNumber;
  };

  // Função para filtrar grupos (apenas grupos próprios se configurado)
  const filteredGroups = groups.filter(group => {
    const matchesSearch = group.subject?.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         group.id?.toLowerCase().includes(searchTerm.toLowerCase());

    if (!antiBanSettings.showOnlyOwnedGroups) {
      return matchesSearch;
    }

    // Mostrar apenas grupos onde o usuário é dono
    return matchesSearch && isGroupOwner(group);
  });

  // Verificar limites de envio
  const checkSendingLimits = () => {
    if (messageStats.sentToday >= antiBanSettings.maxDailyMessages) {
      throw new Error(`Limite diário de ${antiBanSettings.maxDailyMessages} mensagens atingido. Aguarde até amanhã.`);
    }

    if (messageStats.sentThisHour >= antiBanSettings.maxHourlyMessages) {
      throw new Error(`Limite de ${antiBanSettings.maxHourlyMessages} mensagens por hora atingido. Aguarde.`);
    }
