        const connectionState = await checkConnectionState();
        if (connectionState === 'open') {
          setConnectionStatus('Conectado');
          setInstanceCreated(true);
          setQrCode(null);
        } else {
          await getQRCode();
          startStatusChecking();
        }
      } else {
        await createInstanceAndGenerateQR();
